     /**
     * 上传技巧
     * @param params
     * @return
     */
    @RequestMapping(value = "/insertSkill",method = RequestMethod.POST)
    public CodeMsg insertSkill(@RequestParam Map<String, Object> params){
        Skill skill=new Skill();
        skill.setUrl(params.get("skillText").toString());
        skill.setSort_name(params.get("skillType").toString());
        int result=skillService.insertSkill(skill);
        return result>0?CodeMsg.SUCCESS:CodeMsg.ERROR;
    }

    /**
     * 删除上传技巧
     * @param id
     * @return
     */
    @RequestMapping(value = "/deleteSkillById",method = RequestMethod.POST)
    public  CodeMsg deleteSkillById(@RequestParam("id")int id){
        return skillService.deleteSkillById(id)>0?CodeMsg.SUCCESS:CodeMsg.ERROR;
    }

    /**
     * 根据分类删除上传技巧
     * @param sort_name
     * @return
     */
    @RequestMapping(value = "/deleteSkillBySortName",method = RequestMethod.POST)
    public  CodeMsg deleteSkillBySortName(@RequestParam("sort_name")String sort_name){
        return skillService.deleteSkillBySortName(sort_name)>0?CodeMsg.SUCCESS:CodeMsg.ERROR;
    }

